import { Router } from 'express';
import ContactController from '../controllers/ContactController';

const router = Router();

router.get('/', ContactController.getAllContacts);
router.post('/', ContactController.addContact);
router.get('/:id', ContactController.getAContact);
router.put('/:id', ContactController.updatedContact);
router.delete('/:id', ContactController.deleteContact);

export default router;

import ContactService from '../services/ContactService';
import Util from '../utils/Util';

const util = new Util();

class ContactController {
    static async getAllContacts(req, res) {
        try {
            const allContacts = await ContactService.getAllContacts();
            if (allContacts.length > 0) {
                util.setSuccess(200, 'Contacts retrieved', allContacts);
            } else {
                util.setSuccess(200, 'No contacts found');
            }
            return util.send(res);
        } catch (error) {
            util.setError(400, error);
            return util.send(res);
        }
    }

    static async addContact(req, res) {
        if (!req.body.firstname || !req.body.lastname) {
          util.setError(400, 'Please provide complete details');
          return util.send(res);
        }
        const newContact = req.body;
        try {
          const createdContact = await ContactService.addContact(newContact);
          util.setSuccess(201, 'Contact Added!', createdContact);
          return util.send(res);
        } catch (error) {
          util.setError(400, error.message);
          return util.send(res);
        }
      }

    static async updatedContact(req, res) {
        const alteredContact = req.body;
        const { id } = req.params;
        if (!Number(id)) {
            util.setError(400, 'Please input a valid numeric value');
            return util.send(res);
        }
        try {
            const updateContact = await ContactService.updateContact(id, alteredContact);
            if(!updateContact) {
                util.setError(404, `Cannot find contact with the id ${id}`);
            } else {
                util.setSuccess(200, 'Contact Updated', updateContact);
            }
            return util.send(res);
        } catch (error) {
            util.setError(404, error);
            return util.send(res);
        }
    }

    static async getAContact(req, res) {
        const { id } = req.params;

        if(!Number(id)) {
            util.setError(400, 'Please input a valid numeric value');
            return util.send(res);
        }

        try {
            const theContact = await ContactService.getAContact(id);

            if(!theContact) {
                util.setError(404, `Cannot find contact with the id ${id}`);
            } else {
                util.setSuccess(200, 'Found Contact', theContact);
            }
            return util.send(res);
        } catch (error) {
            util.setError(404, error);
            return util.send(res);
        }
    }

    static async deleteContact(req, res) {
        const { id } = req.params;

        if(!Number(id)) {
            util.setError(400, 'Please provide a numeric value');
            return util.send(res);
        }

        try {
            const contactToDelete = await ContactService.deleteContact(id);

            if (contactToDelete) {
                util.setSuccess(200, 'Contact Deleted');
            } else {
                util.setError(404, `Contact with the id ${id} cannot be found`);
            }
            return util.send(res);
        } catch (error) {
            util.setError(400, error);
            return util.send(res);
        }
    }
}

module.exports = ContactController;

import database from '../src/models';

class ContactService {
    static async getAllContacts() {
        try {
            return await database.Contact.findAll();
        } catch (error) {
            throw error;
        }
    }

    static async addContact(newContact) {
        try {
            return await database.Contact.create(newContact);
        } catch (error) {
            throw error;
        }
    }

    static async updateContact(id, updateContact) {
        try {
            const contactToUpdate = await database.Contact.findOne({
                where: { id: Number(id) }
            });

            if (contactToUpdate) {
                await database.Contact.update(updateContact, { where: { id: Number(id) } });

                return updateContact;
            }
            return null;
        } catch (error) {
            throw error;
        }
    }

    static async getAContact(id) {
        try {
            const theContact = await database.Contact.findOne({
                where: { id: Number(id) }
            });

            return theContact;
        } catch (error) {
            throw error;
        }
    }

    static async deleteContact(id) {
        try {
            const contactToDelete = await database.Contact.findOne({ where: { id: Number(id) } });

            if (contactToDelete) {
                const deletedContact = await database.Contact.destroy({
                    where: { id: Number(id) }
                });
                return deletedContact;
            }
            return null;
        } catch (error) {
            throw error;
        }
    }
}

export default ContactService;

import fs from 'fs';
import path from 'path';
import Sequelize from 'sequelize';
import configJson from '../config/config';

const basename = path.basename(__filename);
const env = process.env.NODE_ENV ? process.env.NODE_ENV : 'development';

const config = configJson[env];

console.log('this is the environment: ', env);

const db = {};

let sequelize;
if (config.environment === 'production') {
  sequelize = new Sequelize(
    process.env[config.use_env_variable], config
  );
  sequelize = new Sequelize(
    process.env.DB_NAME,
    process.env.DB_USER,
    process.env.DB_PASS, {
      host: process.env.DB_HOST,
      port: process.env.DB_PORT,
      dialect: 'postgres',
      dialectOption: {
        ssl: true,
        native: true,
      },
      logging: true
    }
  );
} else {
  sequelize = new Sequelize(config.database, config.username, config.password, config);
}

fs
  .readdirSync(__dirname)
  .filter(file => {
    return (file.indexOf('.') !== 0) && (file !== basename) && (file.slice(-3) === '.js');
  })
  .forEach(file => {
    const model = sequelize['import'](path.join(__dirname, file));
    db[model.name] = model;
  });

Object.keys(db).forEach(modelName => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;

export default db;

